import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Form, Button, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import FormContainer from '../components/FormContainer'
import { saveShoppingAddress } from '../actions/cartActions'
import CheckoutSteps from '../components/CheckoutSteps'

const ShippingScreen = () => {
    const cart = useSelector(state =>state.cart)
    const {shippingAddress} = cart
  let history = useNavigate()
  const [address, setAddress] = useState(shippingAddress.address)
  const [city, setCity] = useState(shippingAddress.city)
  const [postalCode, setPostalCode] = useState(shippingAddress.postalCode)
  const [country, setCountry] = useState(shippingAddress.country)

  const dispatch = useDispatch()

  const submitHandler = (e) => {
      e.preventDefault()
      dispatch(saveShoppingAddress({address,city,postalCode,country}))
      history('/payment')
  }

  return (
    <FormContainer>
      <CheckoutSteps step1 step2/>
      <h1>Shipping Details</h1>
      <Form onSubmit={submitHandler}>
        <Form.Group className='mb-2' controlId='address'>
          <Form.Label>Address</Form.Label>
          <Form.Control
            type='text'
            placeholder='Enter Address'
            value={address}
            required
            onChange={(e) => {
              setAddress(e.target.value)
            }}
          ></Form.Control>
        </Form.Group>

        <Form.Group className='mb-2' controlId='city '>
          <Form.Label>City</Form.Label>
          <Form.Control
            type='text'
            placeholder='Enter City'
            value={city}
            required
            onChange={(e) => {
              setCity(e.target.value)
            }}
          ></Form.Control>
        </Form.Group>

        <Form.Group className='mb-2' controlId='postalCode'>
          <Form.Label>Postal Code</Form.Label>
          <Form.Control
            type='text'
            placeholder='Enter Postal Code'
            value={postalCode}
            required
            onChange={(e) => {
              setPostalCode(e.target.value)
            }}
          ></Form.Control>
        </Form.Group>

        <Form.Group className='mb-2' controlId='country'>
          <Form.Label>Country</Form.Label>
          <Form.Control
            type='text'
            placeholder='Enter Country'
            value={country}
            required
            onChange={(e) => {
              setCountry(e.target.value)
            }}
          ></Form.Control>
        </Form.Group>

            <Button type='submit' variant='primary'>
                Continue 
            </Button>

      </Form>
    </FormContainer>
  )
}

export default ShippingScreen
